import React, { useState } from 'react';

function App() {
  const [sales, setSales] = useState([]);
  const [formData, setFormData] = useState({
    valueSold: '',
    installments: '',
    costValue: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newSale = {
      date: new Date().toLocaleDateString(),  // Registro automático da data
      valueSold: formData.valueSold,
      installments: formData.installments,
      costValue: formData.costValue
    };
    setSales([...sales, newSale]);
    setFormData({ valueSold: '', installments: '', costValue: '' });
  };

  return (
    <div className="App">
      <h1>Registro de Vendas</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Valor Vendido:</label>
          <input
            type="number"
            name="valueSold"
            value={formData.valueSold}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Parcelas:</label>
          <input
            type="number"
            name="installments"
            value={formData.installments}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Valor de Custo:</label>
          <input
            type="number"
            name="costValue"
            value={formData.costValue}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Registrar Venda</button>
      </form>

      <h2>Vendas Registradas</h2>
      <table>
        <thead>
          <tr>
            <th>Data</th>
            <th>Valor Vendido</th>
            <th>Parcelas</th>
            <th>Valor de Custo</th>
          </tr>
        </thead>
        <tbody>
          {sales.map((sale, index) => (
            <tr key={index}>
              <td>{sale.date}</td>
              <td>{sale.valueSold}</td>
              <td>{sale.installments}</td>
              <td>{sale.costValue}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
